const cursor=document.querySelector(".cursor"),cursorRadius=Math.round(cursor.getBoundingClientRect().width/2),mouse={x:0,y:0},pos={x:0,y:0},ratio=.06,draw=()=>{pos.x+=.06*(mouse.x-cursorRadius-pos.x),pos.y+=.06*(mouse.y-cursorRadius-pos.y),cursor.style.transform=`translate3d(${pos.x}px ,${pos.y}px, 0)`},update=s=>{mouse.x=s.clientX,mouse.y=s.clientY};function animate(){pos.x+=.06*(mouse.x-cursorRadius-pos.x),pos.y+=.06*(mouse.y-cursorRadius-pos.y),cursor.style.transform=`translate3d(${pos.x}px ,${pos.y}px, 0)`,requestAnimationFrame(animate)}window.addEventListener("mousemove",update,{capture:!1,passive:!0}),requestAnimationFrame(animate);